import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './style.css';

function Login() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const [loggedInUser, setLoggedInUser] = useState(null); // State to store the logged-in user data

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/accounts/api/login/', formData);
      console.log('Login Successful:', response.data);

      // Assuming the backend returns user data upon successful login
      setLoggedInUser(response.data);

      // You can also store the token in local storage or context for future requests
    } catch (error) {
      console.error('Login Failed:', error.response.data);
    }
  };

  return (
    <>
      <Link to="/" className="home-link">
        <button className="home-button">Home</button>
      </Link>
      <h2 className="name-rest">Login</h2>
      <br />
      <br />
      <br />
      <div className="signup-container">
        <form className="signup-form" onSubmit={handleLogin}>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            placeholder="Enter your username"
            onChange={handleChange}
            required
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            placeholder="Enter your password"
            onChange={handleChange}
            required
          />
          <p />

          <button type="submit" className="signup-button">
            Login
          </button>
        </form>
      </div>
      <div className='form-below'>
        Don't have an account?
        <Link to="/Signup">SignUp</Link>
      </div>
      {loggedInUser && (
        <div>
          <h3>Welcome, {loggedInUser.username}!</h3>
          <p>Email: {loggedInUser.email}</p>
          {/* Add more user details if available */}
        </div>
      )}
    </>
  );
}

export default Login;
